if ($setting['varname'] == 'vbblog_featured')
{
	$value = @unserialize($setting['value']);
	$vbblog_featured = array();

	if ($value['type'] == 'manual')
	{
		$vbblog_featured['manual'] = 'checked="checked"';
		$vbblog_featured['blogid'] = intval($value['blogid']) ? intval($value['blogid']) : '';
	}
	else
	{
		$vbblog_featured['random'] = 'checked="checked"';
		$vbblog_featured['username'] = $value['username']; // htmlspecialchar'd on save
		if ($value['startstamp'])
		{
			$startdate = getdate($value['startstamp']);
			$vbblog_featured['start' . $startdate['mon']] = 'selected="selected"';
			$vbblog_featured['startday'] = $startdate['mday'];
			$vbblog_featured['startyear'] = $startdate['year'];
		}
		if ($value['endstamp'])
		{
			$enddate = getdate($value['endstamp']);
			$vbblog_featured['end' . $enddate['mon']] = 'selected="selected"';
			$vbblog_featured['endday'] = $enddate['mday'];
			$vbblog_featured['endyear'] = $enddate['year'];
		}
		$vbblog_featured['refresh' . $value['refresh']] = 'selected="selected"';
	}
}

if ($oldsetting['varname'] == 'vbblog_featured')
{
	if (version_compare($vbulletin->options['templateversion'], '3.7.0 Alpha 1', '>='))
	{
		$setvar =& $settings;
	}
	else
	{
		$setvar =& $vbulletin->GPC['setting'];
	}

	if ($setvar['vbblog_featured']['option'] == 2)
	{
		$larke = array(
			'type'   => 'manual',
			'blogid' => intval($setvar['vbblog_featured']['blogid']),
		);
	}
	else
	{
		$larke = array(
			'type' => 'random'
		);

		if ($setvar['vbblog_featured']['username'])
		{
			if ($user = $vbulletin->db->query_first("
				SELECT userid
				FROM " . TABLE_PREFIX . "user
				WHERE username = '" . htmlspecialchars_uni($setvar['vbblog_featured']['username']) . "'
			"))
			{
				$larke['username'] = htmlspecialchars_uni($setvar['vbblog_featured']['username']);
				$larke['userid'] = $user['userid'];
			}
		}

		if (@checkdate($setvar['vbblog_featured']['startmonth'], $setvar['vbblog_featured']['startday'], $setvar['vbblog_featured']['startyear']))
		{
			$startstamp = mktime(0, 0, 0, $setvar['vbblog_featured']['startmonth'], $setvar['vbblog_featured']['startday'], $setvar['vbblog_featured']['startyear']);
		}
		if (@checkdate($setvar['vbblog_featured']['endmonth'], $setvar['vbblog_featured']['endday'], $setvar['vbblog_featured']['endyear']))
		{
			$endstamp = mktime(23, 59, 59, $setvar['vbblog_featured']['endmonth'], $setvar['vbblog_featured']['endday'], $setvar['vbblog_featured']['endyear']);
		}
		if ($startstamp AND (!$endstamp OR $startstamp < $endstamp))
		{
			$larke['startstamp'] = $startstamp;
		}
		if ($endstamp AND (!$startstamp OR $startstamp < $endstamp))
		{
			$larke['endstamp'] = $endstamp;
		}
		$larke['refresh'] = intval($setvar['vbblog_featured']['refresh']);
	}

	$setvar['vbblog_featured'] = serialize($larke);
	build_datastore('blogfeatured', '');
}

